//Packages
package loopsDemo;

//creating class
public class loops {

	//creating main 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//declaring instance variable
		int myVal=15;
		
		//calling methods
		print();
		value(myVal);
		
	}
	
	//method for printing
	static void print()
	{
		System.out.println("Welcome");
	}
	
	//method for value
	static void value(int myVal)
	{
		System.out.println("Value is ="+ myVal);
	}

}
